import React, { Component } from 'react';

const MAX_PIZZAS = 20;

const eatPizza = (state, props) => {
  const { pizzas } = state;
  return {
    pizzas: pizzas + 1
  };
};

class Controlled extends Component {
  state = {
    pizzas: 1
  };
  render() {
    const { pizzas } = this.state;
    return <button onClick={this.eatPizza}>{`I have aten ${pizzas} 
      ${pizzas === 1 ? "pizza" : "pizzas"}`}</button>;
  };
}

class App extends Component {
  render() {
    return (
      <Controlled />
    );
  }
}

export default App;
